import React, { useState } from 'react';

function App() {
  const [keywords, setKeywords] = useState(['']);
  const [domains, setDomains] = useState(['']);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleKeywordChange = (idx, value) => {
    const newKeywords = [...keywords];
    newKeywords[idx] = value;
    setKeywords(newKeywords);
  };

  const handleDomainChange = (idx, value) => {
    const newDomains = [...domains];
    newDomains[idx] = value;
    setDomains(newDomains);
  };

  const addKeyword = () => setKeywords([...keywords, '']);
  const addDomain = () => setDomains([...domains, '']);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const response = await fetch('http://localhost:5000/search', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ keywords, domains })
    });
    const data = await response.json();
    setResults(data.results);
    setLoading(false);
  };

  return (
    <div style={{ padding: 32 }}>
      <h1>AI Search Tracker</h1>
      <form onSubmit={handleSubmit}>
        <h2>Keywords</h2>
        {keywords.map((kw, idx) => (
          <input key={idx} value={kw} onChange={e => handleKeywordChange(idx, e.target.value)} placeholder="Keyword" style={{ marginBottom: 8, width: 300 }} />
        ))}
        <button type="button" onClick={addKeyword}>Add Keyword</button>
        <h2>Client Domains</h2>
        {domains.map((dm, idx) => (
          <input key={idx} value={dm} onChange={e => handleDomainChange(idx, e.target.value)} placeholder="Domain" style={{ marginBottom: 8, width: 300 }} />
        ))}
        <button type="button" onClick={addDomain}>Add Domain</button>
        <br /><br />
        <button type="submit" disabled={loading}>{loading ? 'Searching...' : 'Search'}</button>
      </form>
      <div style={{ marginTop: 32 }}>
        <h2>Results</h2>
        {results.length === 0 ? <p>No results yet.</p> : (
          results.map((res, idx) => (
            <div key={idx} style={{ marginBottom: 16 }}>
              <strong>Keyword:</strong> {res.keyword}<br />
              <strong>Engine:</strong> {res.engine}<br />
              <strong>Domain Found:</strong> {res.domain_found ? 'Yes' : 'No'}<br />
              {res.domain_found && <strong>Position: {res.position}</strong>}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
